import React from 'react'
import { useState } from 'react'
import Icon from '../../assets/icon.png'

const Auth = () => {
    const[isSignup, SetIsSignup]=useState(false)
    const handleSwitch=()=>{
        SetIsSignup(!isSignup) 
    }
  return (
    <div style={{width:'50%', margin:'0 auto'}}>
        {!isSignup && <img src={Icon} alt='stackoverflow icon' width='50' height='50px'/>}
        <form style={{display:'flex', flexDirection:'column'}}>
            <label htmlFor='email'>
                <h4>Email</h4>
                <input type='email' name='email' id='email'></input>
            </label>
            <label htmlFor='password'>
                <div style={{display:'flex', justifyContent:'space-evenly'}}><h4>Password</h4>
                {!isSignup && <p style={{color:'#007ac6', fontSize:'13px'}}>Forgot Password?</p>}
                </div>
                <input type='password' name='password' id='password'></input>
                
                    {isSignup && <p>Passwords must contain at least eight characters,<br/> including at least 1 letter and 1 number.</p> }
                
            </label>
            <button type='submit' className='auth-btn'>
                {isSignup? 'Sign up':'Log in'}
            </button>
            <p>
                {isSignup?'Already have an account?':`Don't have an account?`}
            <button type='button' onClick={handleSwitch}>
            {isSignup?'Log in':'Sign up'}
            </button>
           </p>
        </form>
    </div>
  )
}

export default Auth
