import React from 'react'
import './Question.css'
import { Link,useParams } from 'react-router-dom'
import upVote from '../../assets/upVote.svg'
import downVote from '../../assets/downVote.svg'
import Avatar from '../../components/Avatar/Avatar'
import DisplayAnswer from './DisplayAnswer'

var QuestionDetails = () => {
    const {id} = useParams()
    console.log(id)
    const questionList=[{
        id:'1',
        upVotes:3,
        downVotes:2,        
        noOfAnswer: 2,
        questionTitle:"what is a function",
        questionBody:"It meant to be",
        questionTags:["java","nodejs","reactjs"],
        userPosted:"mano",
        askedOn:"jan1",
        answer:[{
          answerBody:"Answer",
          userAnswered:'Kumar',
          answerdOn:'jan 2',
          userId:2
        }]
    
      },
      {
        id:'2',
        upVotes:3,
        downVotes:2,        
        noOfAnswer: 2,
        questionTitle:"what is a function",
        questionBody:"It meant to be",
        questionTags:["java","nodejs"],
        userPosted:"harry",
        askedOn:"jan1",
        answer:[{
          answerBody:"Answer",
          userAnswered:'harry',
          answerdOn:'jan 2',
          userId:3 }]
      },
      {
        id:'3',
        upVotes:3,
        downVotes:2,        
        noOfAnswer: 3,
        questionTitle:"what is a function",
        questionBody:"It meant to be",
        questionTags:["java","nodejs","reactjs","mongodb"],
        userPosted:"mano",
        askedOn:"jan1",
        answer:[{
          answerBody:"Answer",
          userAnswered:'jack',
          answerdOn:'jan 2',
          userId:4 }]
      }]
    // const questionList = null
    
  return (   
    <div className='question-detail-page'>
     {
        questionList === null ? 
        <h1>Loading..</h1> :
        <>
        {
            questionList.filter(question => question.id === id).map(question =>(
                <div key={question.id}>
                    <section className='question-details-container'>
                        <h1>{question.questionTitle}</h1>
                                <div className="question-votes">
                                <img src={upVote} alt='upvote' width='18'/>
                                <p>{question.upVotes - question.downVotes}</p>
                                <img src={downVote} alt='downvote' width='18'/>
                            </div>
                            <div style={{width:'100%'}}>
                                <h4>hello world</h4>
                                <p className='question-body'>{question.questionBody}</p>
                                <div className="question-details-tags">
                                     {
                                        question.questionTags.map((tag)=>(
                                            <p key={tag}>{tag}</p>
                                        ))
                                    }
                                </div>
                                <div className="question-action-uesr">
                                     <div>
                                         <button type='button'>Share</button>
                                         <button type='button'>Delete</button>
                                     </div>
                                     <div>
                                         <p>asked {question.askedOn}</p>
                                         <Link to={`/user/${question.askedOn}`} className='user-link' style={{color:'#0086d8'}}>
                                             <Avatar backgroundColor='orange' px='8px' py='5px'>
                                                 {question.userPosted.charAt(0).toUpperCase()}
                                             </Avatar>
                                             <div>
                                                 {question.userPosted}
                                             </div>
                                         </Link>
                                     </div>
                                 </div>
                             </div>
                         </section>
                         {
                             question.noOfAnswer!==0 && (
                                 <section>
                                     <h3>{question.noOfAnswer} answer</h3>
                                     <DisplayAnswer key={question._id} question={question}/>
                                 </section>
                             )
                         }
                         <section className='post-ans-container'>
                             <h3>Your answer </h3>
                             <form>
                                 <textarea id=' ' cols='30' rows='10' ></textarea>
                                 <input type='submit' className='post-ans-btn' value='Post your answer'/>
                             </form>
                             <p>
                                 Browse other Question tagged
                                 {
                                     question.questionTags.map((tag)=>(
                                         <Link to='/Tags' key={tag} className='ans-tag'>{tag}</Link>
                                     ))
                                 } or 
                                 <Link to='/AskQuestion' style={{textDecoration:'none', color:'#009dff'}}>Ask your question</Link>                            
                             </p>
                    </section>
                 </div>
            ))           
       }
        </>
     }   
    </div>


    //    <div className='question-detail-page'>
